import React, { useEffect, useState } from 'react';
import { NavLink, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';

const NewReview = () => {
    const {id} = useParams()
    const dispatch = useDispatch()
    const product = useSelector(state => state.products.id)
    const [stars, setStars] = useState()
    const [headline, setHeadline] = useState()
    const [details, setDetails] = useState()
    console.log(product, "PRODUCTREVIEW")

    const setRating = (num) => {
        setStars(num)
    }

    const updateHeadline = (e) => {
        setHeadline(e.target.value)
    }

    const updateDetails = (e) => {
        setDetails(e.target.value)
    }

    return (
        // <h1>hi</h1>
        <div className='reviewForm'>
            <div>Create Review</div>
            <div className='picInfo'>
                <img src={product[Object.keys(product)[0]].photourl}></img>
                <h3>{product[Object.keys(product)[0]].name}</h3>
            </div>
            <div className='rating'>
                <h1>Overall rating</h1>
                <img onClick={() => setRating(1)} src='data:image/svg+xml;base64,'></img>
                <img onClick={() => setRating(2)} src='data:image/svg+xml;base64,'></img>
                <img onClick={() => setRating(3)} src='data:image/svg+xml;base64,'></img>
                <img onClick={() => setRating(4)} src='data:image/svg+xml;base64,'></img>
                <img onClick={() => setRating(5)} src='data:image/svg+xml;base64,'></img>
            </div>
            <div className='headline'>
                <h1>Add a headline</h1>
                <input
                type='text'
                placeholder='What is most important to know?'
                value={headline}
                onChange={updateHeadline}
                ></input>
            </div>
            <div className='details'>
                <h1>Add a written review</h1>
                <input
                    type='text'
                    placeholder='What did you like or dislike? What did you use this product for?'
                    value={details}
                    onChange={updateDetails}
                ></input>
            </div>
            <button type='submit'>Submit</button>
        </div>
    )
}

export default NewReview;
